mapper.select:
SELECT ID
FROM MEMBER
WHERE ID=#{id}

SELECT EMAIL
FROM MEMBER
WHERE EMAIL=#{email}

SELECT PHONE
FROM MEMBER
WHERE PHONE=#{phone}

SELECT *
FROM MEMBER
WHERE ID=#{id} AND PWD=#{pwd}

mapper.update:
UPDATE MEMBER SET
AUTHKEY=#{authkey}
WHERE ID=#{id}

UPDATE MEMBER SET
AUTHSTATUS=1
WHERE EMAIL=#{email}

mapper.select:
SELECT ID
FROM MEMBER
WHERE name=#{name} AND EMAIL=#{email}

mapper.update:
UPDATE MEMBER SET
PWD=#{pwd}
WHERE id=#{id} AND EMAIL=#{email}

mapper.select:
select NVL(COUNT(*), 0)
from member
where id=#{id} or email=#{email}
